import React, { useEffect } from "react";

import './Body.css'; 

import myWorkDashboards from './myWorkDashboards.png';
import myWorkWizards from './myWorkWizards.png';
import myWorkApps from './myWorkApps.png';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import WorkSection from "./WorkSection.js";
import SkillSection from "./SkillSection.js";

function Body() {

  useEffect(() => {

    const bodyContainer = document.querySelector('.bodyContainer');

    window.addEventListener('scroll', function (event) {
      if (isInViewport(bodyContainer)) {
        bodyContainer.classList.add('showScroller');
      }
    }, false);

  });

  const isInViewport = (elem,e) => {

    if (elem != null) {

      var bounding = elem.getBoundingClientRect();
      return (
          bounding.top >= 0 &&
          bounding.left >= 0 &&
          bounding.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
          bounding.right <= (window.innerWidth || document.documentElement.clientWidth)
      );

    }

  };

  return (

    <div className="bodyContainer">

      <Container className="workSectionScrollerContainer">
        <Col>
          <Row>
            <div className="workSectionScroller">
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-arrow-down-circle-fill" viewBox="0 0 16 16">
                <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0M8.5 4.5a.5.5 0 0 0-1 0v5.793L5.354 8.146a.5.5 0 1 0-.708.708l3 3a.5.5 0 0 0 .708 0l3-3a.5.5 0 0 0-.708-.708L8.5 10.293z"/>
              </svg>
            </div>
          </Row>
        </Col>
      </Container>

      <WorkSection />

      <SkillSection />

    </div>

  );
}

export default Body;
