import React, { useEffect } from "react";

import './Body.css'; 

import myWorkDashboards from './myWorkDashboards.png';
import myWorkWizards from './myWorkWizards.png';
import myWorkApps from './myWorkApps.png';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import WorkSection from "./WorkSection.js";
import SkillSection from "./SkillSection.js";

function Body() {

  useEffect(() => {

    window.addEventListener('scroll', addScroller);

    return () => {
      window.removeEventListener('scroll', addScroller);
    };

  });

  const addScroller = (e) => {

    const workSection = document.getElementById('myWork');
    const skillsSection = document.getElementById('mySkills');

    if (workSection != null && skillsSection != null) {

      (isElementInViewport(workSection)) ? document.querySelector('.bodyContainer').classList.add('add-scroller') : document.querySelector('.bodyContainer').classList.remove('add-scroller');

    }

  };


function isElementInViewport (el) {

    //account for fixed navbar
    const navbar = document.getElementById('TopNav');      
    const navbarHeight = navbar.clientHeight;

    const rect = el.getBoundingClientRect();

    return (
      (rect.bottom + navbarHeight) >= 0  && 
      (rect.top + navbarHeight) <= (window.innerHeight || document.documentElement.clientHeight) 
    );
}

  return (

    <div className="bodyContainer">

      <WorkSection />

      <SkillSection />

    </div>

  );
}

export default Body;
