import React, { useEffect } from "react";
import './Header.css'; 
import headerLogoBlack from './logo-icon-black-nobg.png';
import headerLogoWhite from './logo-icon-white-nobg.png';
import headerLogoBeige from './logo-icon-beige-nobg.png';
import myPic from './me.png';

const Header = () => {
  // Sticky Menu Area
  useEffect(() => {
    window.addEventListener("scroll", isSticky);
    return () => {
      window.removeEventListener("scroll", isSticky);
    };
  });

  /* Method that will fix header after a specific scrollable */
  const isSticky = (e) => {
    const header = document.getElementById("header-section");
    const scrollTop = window.scrollY;
    scrollTop >= 77
      ? header.classList.add("is-sticky")
      : header.classList.remove("is-sticky");
  };
  return (
      <header id="header-section">

        <div id="header-section-nav-container">
          <div className="nav-item">
            <div id="greetingPic">
              <img height="100" src={myPic} alt="Zolaikha Freidah Logo"/>
            </div>
          </div> 

          <div className="nav-item">
            <i className="fi fi-ss-display-code"></i>
            <span>My Skills</span>
          </div> 

          <div className="nav-item">
            <i className="fi fi-bs-pencil-paintbrush"></i>
            <span>My Work</span>
          </div>

          <div className="nav-item">
            <i className="fi fi-ss-comment-medical"></i>
            <span>Contact</span>
          </div>
        </div>

      </header>
  );
};

export default Header;
