import React, { useEffect } from "react";

import './Body.css'; 

import myWorkDashboards from './myWorkDashboards.png';
import myWorkWizards from './myWorkWizards.png';
import myWorkApps from './myWorkApps.png';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import WorkSection from "./WorkSection.js";
import SkillSection from "./SkillSection.js";

function Body() {

var el = document.querySelector('bodyContainer');

function isElementInViewport (el) {

    var rect = el.getBoundingClientRect();

  return (
    rect.bottom >= 0 && 
    rect.right >= 0 && 
    rect.top <= (window.innerHeight || document.documentElement.clientHeight) && 
    rect.left <= (window.innerWidth || document.documentElement.clientWidth)
  );
}

function onVisibilityChange(el, callback) {
    var old_visible;
    return function () {
        var visible = isElementInViewport(el);
        if (visible != old_visible) {
            old_visible = visible;
            if (typeof callback == 'function') {
                callback();
            }
        }
    }
}

var handler = onVisibilityChange(el, function() {
    el.classList.add('show-scroller');
});


if (window.addEventListener) {
    addEventListener('DOMContentLoaded', handler, false);
    addEventListener('load', handler, false);
    addEventListener('scroll', handler, false);
    addEventListener('resize', handler, false);
} else if (window.attachEvent)  {
    attachEvent('onDOMContentLoaded', handler); // Internet Explorer 9+ :(
    attachEvent('onload', handler);
    attachEvent('onscroll', handler);
    attachEvent('onresize', handler);
}

  return (

    <div className="bodyContainer">

      <Container className="workSectionScrollerContainer">
        <Col>
          <Row>
            <div className="workSectionScroller">
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-arrow-down-circle-fill" viewBox="0 0 16 16">
                <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0M8.5 4.5a.5.5 0 0 0-1 0v5.793L5.354 8.146a.5.5 0 1 0-.708.708l3 3a.5.5 0 0 0 .708 0l3-3a.5.5 0 0 0-.708-.708L8.5 10.293z"/>
              </svg>
            </div>
          </Row>
        </Col>
      </Container>

      <WorkSection />

      <SkillSection />

    </div>

  );
}

export default Body;
